Adafruit_SSD1306 display(SCREEN_WIDTH, SCREEN_HEIGHT, &Wire, OLED_RESET);

// Переменные для меню
int currentSelection = 0; // Текущая выбранная строка меню
const int menuItems = 3;  // Количество пунктов меню

void setup() {
  // Инициализация I2C на ESP32
  Wire.begin(8, 9); // SDA = GPIO 21, SCL = GPIO 22
  Wire.setClock(100000); // Установим частоту I2C на 100kHz

  // Инициализация дисплея
  if (!display.begin(SSD1306_SWITCHCAPVCC, SCREEN_ADDRESS)) {
    Serial.println(F("SSD1306 allocation failed"));
    for (;;); // Остановить выполнение если дисплей не инициализирован
  } else {
    Serial.println(F("ALL OK"));
  }

  // Очистка экрана
  display.clearDisplay();

  // Настройка пинов кнопок
  pinMode(DOWN_BUTTON, INPUT_PULLUP);
  pinMode(UP_BUTTON, INPUT_PULLUP);
  pinMode(MIDDLE_BUTTON, INPUT_PULLUP);

  // Отображаем начальное меню
  displayMenu();
}

int frame = 0;
bool animating = false; // Переменная для отслеживания состояния анимации

void loop() {
  // Проверка нажатия кнопки вниз
  if (digitalRead(DOWN_BUTTON) == LOW) {
    currentSelection = (currentSelection + 1) % menuItems; // Цикл по меню
    displayMenu();
    delay(200); // Задержка для предотвращения дребезга кнопок
  }

  // Проверка нажатия кнопки вверх
  if (digitalRead(UP_BUTTON) == LOW) {
    currentSelection = (currentSelection - 1 + menuItems) % menuItems; // Цикл по меню
    displayMenu();
    delay(200); // Задержка для предотвращения дребезга кнопок
  }

  // Проверка нажатия кнопки выбора (MIDDLE_BUTTON)
  if (digitalRead(MIDDLE_BUTTON) == LOW) {
    if (animating) {
      animating = false; // Выход из анимации
      displayMenu(); // Вернуться в меню
    } else {
      handleSelection(); // Обрабатываем выбор
    }
    delay(200); // Задержка для предотвращения дребезга кнопок
  }

  // Если анимация активна, продолжаем её
  if (animating) {
    animateHeart(); // Запускаем анимацию
  }
}

// Функция для отображения меню
void displayMenu() {
  display.clearDisplay(); // Очистка экрана

  // Вывод пунктов меню
  display.setTextSize(1);
  display.setTextColor(SSD1306_WHITE);

  display.setCursor(0, 0);
  if (currentSelection == 0) {
    display.print(F("1)Print hello <")); // Пункт выбран
  } else {
    display.print(F("1)Print hello"));
  }

  display.setCursor(0, 16);
  if (currentSelection == 1) {
    display.print(F("2)Print SHS <")); // Пункт выбран
  } else {
    display.print(F("2)Print SHS"));
  }
  display.setCursor(0, 32);
  if (currentSelection == 2) {
    display.print(F("3)Print Gift For Rusik <")); // Пункт выбран
  } else {
    display.print(F("3)Print Gift For Rusik"));
  }
  display.display(); // Обновляем экран
}

// Функция для обработки выбора пункта меню
void handleSelection() {
  display.clearDisplay(); // Очистка экрана

  display.setCursor(0, 0); // Устанавливаем курсор в начало

  if (currentSelection == 0) {
    display.print(F("Hello !")); // Если выбран пункт "Print hello"
  } else if (currentSelection == 1) {
    display.print(F("Sasha Heartwarming Smile !")); // Если выбран пункт 
  } else if (currentSelection == 2) {
    animating = true; // Запускаем анимацию, если выбран пункт "Print my feeling to Sinlan"
    frame = 0; // Сбрасываем кадр анимации
  }

  display.display(); // Обновляем экран
}

// Простая анимация сердцебиения
void animateHeart() {
  display.clearDisplay();
  display.drawBitmap(40, 8, frames[frame], FRAME_WIDTH, FRAME_HEIGHT, 1);
  display.display();
  frame = (frame + 1) % FRAME_COUNT;
  delay(FRAME_DELAY);
}
